import React, { useState, useEffect, useRef } from 'react'
import { Line } from 'react-chartjs-2'

export default function SoundChart(props) {
    const [data, setData] = useState(props.data)
    const [current, setCurrent] = useState()
    const chartRef = useRef()



    const updateData = (canvas) => {
        console.log(canvas)
    if (canvas !== undefined){
        var ctx = canvas.getContext('2d')
        return {
            labels: ['Red', 'Blue', 'Yellow', 'Green', 'Purple', 'Orange'],
            datasets: [{
                label: '# of Votes',
                data: [12, 19, 3, 5, 2, 3],
                backgroundColor: [
                    'rgba(255, 99, 132, 0.2)',
                    'rgba(54, 162, 235, 0.2)',
                    'rgba(255, 206, 86, 0.2)',
                    'rgba(75, 192, 192, 0.2)',
                    'rgba(153, 102, 255, 0.2)',
                    'rgba(255, 159, 64, 0.2)'
                ],
                borderColor: [
                    'rgba(255, 99, 132, 1)',
                    'rgba(54, 162, 235, 1)',
                    'rgba(255, 206, 86, 1)',
                    'rgba(75, 192, 192, 1)',
                    'rgba(153, 102, 255, 1)',
                    'rgba(255, 159, 64, 1)'
                ],
                borderWidth: 1
                }],
            options: {
                scales: {
                    y: {
                        beginAtZero: true
                    }
                }
            }
        }
    }

    }

    useEffect(() => {
        console.log("Triggered Refresh")
        updateData(chartRef)
        setCurrent(data)
    }, [props.data])


    return (

        <canvas id="myChart" ref={chartRef}></canvas>

    )
}
