import React, { useState, useEffect, useRef } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';

import { withRouter, NavLink } from 'react-router-dom';
import Recorder from './Recorder.js'
import Player from './Player.js';
import axios from 'axios';
import { processAudioData } from '../utils/processAudio.js'
import { getPitchScatterData, resetCanvas, newPitchChart, newCanvas } from './SoundChart.js';
import { getCSRF } from '../utils/csrfHelper'


const useStyles = makeStyles((theme) => ({
  content: {
    justifyContent: "center",
    display: "flex"
  },
    paper: {
        padding: theme.spacing(2),
        flexDirection: "row",
        minWidth: '100vw',
        minHeight: '80vh',
        display:"flex-box",
        elevation: 3,
        alignItems:"center"
    },
    container: {
        position: 'relative',
        height: '100%',
        justifyContent: "center"
    },
    button: {
        position: 'absolute',
        bottom:'0',
        right: '0'
    },
    grid: {
        height: "50%",
        justifyContent:"space-evenly",
        spacing:"0",
        alignItems:"flex-end",
        direction:"column",
        minHeight:"100%"
    },

    chart: {
        justifyContent:"space-evenly",
        spacing:"0",
        alignItems:"flex-end",
        direction:"column",
        minHeight:"40%",
        marginBottom: "1vh",
        minWidth:"35vw",
    }
}));

const ProdTask = (props) => {
    const classes = useStyles();
    let [src, setCurrentSrc] = useState()
    let [processedData, setProcessedData] = useState(null)


    let [chartA, setChartA] = useState()
    let [chartB, setChartB] = useState()


    let responseDataRef = useRef(null);
    let questionDataRef = useRef(null);

    let handleChange = (data) => {
        let newCanvas = resetCanvas('response-data', 'response-data-container', chartB)
        axios({
            method: "post",
            url: "/api/process/",
            data: JSON.stringify(data),
            headers: {"Content-Type": "application/octet-stream"}
        }).then((response) => { 
            console.log("called processed audio")
            let pitchData = getPitchScatterData(JSON.stringify(response.data))
            console.log9
            setChartB(newPitchChart(newCanvas, pitchData))
            setProcessedData(pitchData)
         })
    }

    useEffect(() => {
        // Update the document title using the browser AP
        if (src === undefined) { 
            console.log("No data recorded") 
        } else {
        console.log("called src change")
        handleChange(src)

    }},[src]);

    useEffect(() => {
        // Update the document title using the browser API (next action... taskType determines the element to show)
        if (processedData === null) { 
            console.log("No changes recorded") 
        } else {
        console.log("Processed data changed:", processedData)}
        },[processedData]);

    useEffect(() => {

        const taskData = () => {
            "..."
        }
        console.log("new charts")
        setChartA(newPitchChart(questionDataRef.current, []))
        setChartB(newPitchChart(responseDataRef.current, []))
    },[]);

    return (
        <div className={classes.content}>
            <Paper className={classes.paper}>
                <Grid container className={classes.grid}>
                    <Grid item>
                        <Paper id="question-data-container" className={classes.chart}>  
                            <canvas id="question-data" ref={questionDataRef}></canvas>
                        </Paper>
                        <Player url={"https://intonation-trainer.s3.us-east-2.amazonaws.com/test-audio.mp3"}/>
                    </Grid>
                    <Grid item>
                        <Paper id="response-data-container" className={classes.chart}>
                            <canvas id="response-data" ref={responseDataRef}></canvas>
                        </Paper>
                        <Recorder sets={handleChange}/>
                    </Grid>
                    <Grid item>
                        <Button variant="outlined" >Welcome</Button>
                    </Grid>
                </Grid>
            </Paper>
        </div>
        );
    }
export default withRouter(ProdTask)
