import React, { useState, useEffect, useRef } from 'react'
import { Chart, registerables } from 'chart.js'

Chart.register(...registerables);

const getData = (processedData) => {
    const time = processedData['time']
    const pitch = processedData['pitch']

    const datasets = [{
      label: 'Pitch Dataset',
      data: time.map((x, i) => ({ x, y: pitch[i] })),
      backgroundColor: 'rgb(255, 99, 132)'
    }]
    return datasets

  };

const getConfig = (data) => {  
    const config = {
        type: 'scatter',
        data: data,
        options: {
          scales: {
            x: {
              type: 'linear',
              position: 'bottom'
            }
          }
        }
      };
    return config
}

