import React, { useState, useEffect, useRef } from 'react'
import { Chart, registerables } from 'chart.js'

Chart.register(...registerables);

const skipped = (ctx, value) => ctx.p0.skip || ctx.p1.skip ? value : undefined;
const down = (ctx, value) => ctx.p0.parsed.y > ctx.p1.parsed.y ? value : undefined;

export const getPitchScatterData = (processedData) => {

    const data = processedData['data'] || []
    for (let i = 0; i < data.length; i++) {
        text += cars[i] + "<br>";
      }
    const time = data['time'].map(function(value) { return value == 0 ? NaN : value; })
    const pitch = data['pitch'].map(function(value) { return value == 0 ? NaN : value; })

    const pData = time.map((x, i) => ({ x, y: pitch[i] }))

    console.log(pData)

    return pData
}

export const resetCanvas = (id, container_id) => {
    document.getElementById(id).remove()
    const newCanvas = document.createElement('canvas')
    newCanvas.setAttribute('id', id)
    document.getElementById(container_id).append(newCanvas)
    return newCanvas
}

export const newPitchChart = ( element, data ) => {
    var ctx = element.getContext('2d')

    const myChart = new Chart(ctx, {
            type: 'scatter',
            data: {
                datasets: [{
                    label: 'Pitch Frequency',
                    data: data,
                    borderWidth: 1,
                    backgroundColor: 'rgb(255, 99, 132)'
                }]
            },
            options: {
                scales: {
                    y: { },
                    x: { }
                }
            }
        });
    return myChart
}

export const updateChart = (chart, data) => {
    const processedData = getPitchScatterData(data)
    chart.data.datasets.data = data
    chart.update()
}

export default newPitchChart
