import React, { useState, useEffect, useRef } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';

import { withRouter, NavLink } from 'react-router-dom';
import Recorder from './Recorder.js'
import Player from './Player.js';
import { processAudioData } from '../utils/processAudio.js'
import { getPitchScatterData, resetCanvas, newPitchChart, newCanvas } from './SoundChart.js';
import { getCSRF } from '../utils/csrfHelper'


const useStyles = makeStyles((theme) => ({
  content: {
    justifyContent: "center",
    display: "flex"
  },
    paper: {
        padding: theme.spacing(2),
        flexDirection: "row",
        minWidth: '100vw',
        minHeight: '80vh',
        display:"flex-box",
        elevation: 3,
        alignItems:"center"
    },
    container: {
        position: 'relative',
        height: '100%',
        justifyContent: "center"
    },
    button: {
        position: 'absolute',
        bottom:'0',
        right: '0'
    },
    grid: {
        height: "50%",
        justifyContent:"space-evenly",
        spacing:"0",
        alignItems:"flex-end",
        direction:"column",
        minHeight:"100%"
    },

    chart: {
        justifyContent:"space-evenly",
        spacing:"0",
        alignItems:"flex-end",
        direction:"column",
        minHeight:"40%",
        marginBottom: "1vh",
        minWidth:"35vw",
    }
}));

const ProdTask = (props) => {
    let [src, setCurrentSrc] = useState()
    let [staged, setStagedData] = useState()
    let [processedData, setProcessedData] = useState(null)

    let [chartA, setChartA] = useState()
    let [chartB, setChartB] = useState()

    const classes = useStyles();
    const responseDataRef = useRef(null);
    const questionDataRef = useRef(null);

    const handleChange = () => {
        setCurrentSrc(staged)     // console.log(src)
    }

    const processResponseAudioData = (data) => {
        // Expects an array buffer, and returns converted pitch data. 
        var xhr = new XMLHttpRequest()
        xhr.open("POST", "/api/process/", true);
        xhr.setRequestHeader("X-CSRFToken", getCSRF()); 
        //Send the proper header information along with the request
        xhr.setRequestHeader("Content-Type", "application/octet-stream");

        xhr.onreadystatechange = function() { // Call a function when the state changes.
            if (this.readyState === XMLHttpRequest.DONE && this.status === 200) {
                var response = JSON.parse(xhr.responseText);
                console.log(response)
                const newCanvas = resetCanvas('response-data', 'response-data-container')
                newPitchChart(newCanvas, getPitchScatterData(response))
                setProcessedData(response)
            }
        }  
        xhr.send(JSON.stringify(data))
    }

    useEffect(() => {
        // Update the document title using the browser AP
        if (src === undefined) { 
            console.log("No data recorded") 
        } else {
        processResAudioData(src)

    }},[src]);

    useEffect(() => {
        // Update the document title using the browser API (next action...)
        if (processedData === null) { 
            console.log("No changes recorded") 
        } else {
        console.log("Processed data changed:", processedData)}
        },[processedData]);

    useEffect(() => {
        setChartA(newPitchChart(questionDataRef.current, []))
        setChartB(newPitchChart(responseDataRef.current, []))
        // Update the document title using the browser AP
    },[]);

    return (
        <div className={classes.content}>
            <Paper className={classes.paper}>
                <Grid container className={classes.grid}>
                    <Grid item>
                        <Paper id="question-data-container" className={classes.chart}>  
                            <canvas id="question-data" ref={questionDataRef}></canvas>
                        </Paper>
                        <Player/>
                    </Grid>
                    <Grid item>
                        <Paper id="response-data-container" className={classes.chart}>
                            <canvas id="response-data" ref={responseDataRef}></canvas>
                        </Paper>
                        <Recorder sets={setStagedData}/>
                    </Grid>
                    <Grid item>
                        <Button variant="outlined" onClick={handleChange}>Welcome</Button>
                    </Grid>
                </Grid>
            </Paper>
        </div>
        );
    }
export default withRouter(ProdTask)
