import React, { useState, useEffect, useRef } from 'react'
import { Chart, registerables } from 'chart.js'

Chart.register(...registerables);

const getData = (processedData) => {
    if (processedData) {
    const data = processedData['data'] || []
    const time = data['time']
    const pitch = data['pitch']
    const pData = time.map((x, i) => ({ x, y: pitch[i] }))

    console.log(pData)

    const datasets = [{
      label: 'Pitch Dataset',
      data:[ {x:1,y:1}, {x:1,y:1}, {x:1,y:1},{x:1,y:1} ],
      backgroundColor : 'rgba(255,0,0,0.5)',
    }]
    console.log(datasets)
    return datasets
}
const datasets = [{
    label: 'Pitch Dataset',
    data:[ {x:1,y:1}, {x:1,y:1}, {x:1,y:1},{x:1,y:1} ],
    backgroundColor : 'rgba(255,0,0,0.5)',
  }]
return datasets
  };

const getConfig = (data) => {  
    const config = {
        type: 'scatter',
        data: data,
        options: {
          scales: {
            y: {
                type: 'linear'

            },
            x: {
                type: 'linear'
            }
          }
        }
      };
    return config
}

export const createChart = (parent) => {
    const chart = document.createElement('canvas');
    chart.setAttribute("id", "Chart");
    parent.appendChild(chart)
    return chart
}

export const newChart = (element) => {
    const myChart = new Chart(
        element
    );
    return myChart
}

export const updateChart = (chart, data) => {
    const processedData = getData(data)
    const config = getConfig(processedData)
    chart.options = config
    chart.update() 
}


